mapper.select:
SELECT cdm.id,cdm.pid,cdm.goods_md5,cdm.remote_path FROM custom_goods_md5 cdm
WHERE 1=1
    AND cdm.is_delete=0
    AND cdm.catgrid !=0

    AND cdm.user_operation=#{Change_user}


    AND cdm.ocr_need_delete=1
    AND cdm.ocr_need_date=0


    AND cdm.ocr_need_date=#{state}
    AND cdm.ocr_need_delete=#{state}


    AND cdm.catgrid=#{imgtype}

limit #{page},35

SELECT id,pid,remote_path,create_time
FROM `custom_goods_md5`
WHERE is_delete=1
AND ocr_need_delete=1
AND ocr_need_date=1

    AND pid=#{pid}


     AND create_time>=#{startTime}


     AND #{endTime}>=create_time

    order by create_time DESC
    limit #{startNum},#{endNum}

SELECT count(1) FROM `custom_goods_md5` WHERE is_delete=1 AND ocr_need_date=1
AND pid=#{pid}
AND create_time>=#{startTime}
AND #{endTime}>=create_time

SELECT count(1)
FROM custom_goods_md5 cdm
WHERE 1=1
AND cdm.is_delete=0
AND cdm.catgrid !=0

    AND cdm.ocr_need_date=0
    AND cdm.ocr_need_delete=1


    AND cdm.ocr_need_date=#{state}
    AND cdm.ocr_need_delete=#{state}


    AND cdm.user_operation=#{Change_user}


and cdm.catgrid=#{imgtype}

mapper.update:
update custom_goods_md5 SET
    ocr_need_delete=2,
    ocr_need_date=2,
user_operation=#{userName}
where

    id = ${i.id}
