mapper.select:
select total_expenditure totalExpenditure, total_revenue totalRevenue, goods_count goodsCount, average_price averagePrice, profit_loss profitLoss, categroy_num categroyNum, order_num orderNum, freight freight FROM report_info ri,general_report gr where ri.genral_report_id = gr.id
and gr.breport_id = #{breportId,jdbcType=INTEGER}
and report_year = #{reportYear,jdbcType=VARCHAR}
and report_month = #{reportMonth,jdbcType=VARCHAR}
and report_week = #{reportWeek,jdbcType=VARCHAR}
and report_week is null
and report_day = #{reportDay,jdbcType=VARCHAR},
and report_day is null

SELECT COUNT(a.category_id) AS cateAmount,SUM(a.buyAmount) AS buyAmount,SUM(salesAmount) AS salesAmount,SUM(salesCount) AS salesCount,CAST(SUM(salesAmount)/SUM(a.salesCount) AS DECIMAL(18,2)) AS avgSalesPrice, CAST((SUM(salesAmount)-SUM(a.buyAmount))/SUM(salesAmount) AS DECIMAL(18,2)) AS profitLoss FROM ( SELECT c.en_name,c.category_id, CAST(SUM(od.yourorder*od.goodsprice)*6.3 AS DECIMAL(18,2)) AS salesAmount, CAST(SUM(od.yourorder*od.goodsprice)*6.3/SUM(od.yourorder) AS DECIMAL(18,2)) AS avgSalesPrice, CAST(sum(ops.buycount*ops.goods_p_price) AS DECIMAL(18,2)) AS buyAmount, SUM(od.yourorder) AS salesCount,SUM(ops.buycount) AS buyCount, CAST((SUM(od.yourorder*od.goodsprice)*6.3-sum(ops.buycount*ops.goods_p_price))/sum(od.yourorder*od.goodsprice)*6.3/100 AS DECIMAL(18,2)) AS profitLoss FROM order_details od INNER JOIN orderinfo oi ON od.orderid=oi.order_no INNER JOIN 1688_category c ON od.goodscatid=c.category_id LEFT JOIN order_product_source ops ON od.orderid=ops.orderid AND od.goodsid=ops.goodsid WHERE od.state<2 AND oi.state>0 AND oi.state<6 AND c.category_id<>0
and left(oi.create_time,7)=#{time}
GROUP BY od.goodscatid ) a
