SELECT rd.id, rd.customer_info as customerorder, rd.1688_order as a1688Order, rd.1688_shipno as a1688Shipno, rd.item, rd.item_number as itemNumber, rd.apply_user as applyUser, rd.apply_time as applyTime, rd.opt_user as optUser, rd.opt_time as optTime, rd.shipno, rd.change_shipno as changeShipno, rd.State, rd.end_time as endTime, rd.return_reason as returnReason, rd.barcode, rd.return_number as returnNumber, rd.tb_id as tbId, rd.sku, rd.returntime, rd.reason, rd.actual_money, rd.refund_time, rd.refund_money, rd.differences, th.delivery_date as signtime, th.orderdate as placeDate, th.seller as sellerpeo, th.itemqty, th.imgurl, th.itemname FROM return_display as rd LEFT JOIN taobao_1688_order_history as th on rd.tb_id=th.id where 1=1
AND rd.opt_user=#{nameString}
AND rd.state=#{state}
AND (rd.shipno=#{a1688Shipno} or rd.1688_shipno=#{a1688Shipno} or rd.change_shipno=#{a1688Shipno})
AND (rd.opt_time>#{optTimeStart} or rd.apply_time>#{optTimeStart})
AND (rd.opt_time<#{optTimeEnd} or rd.apply_time<#{optTimeEnd})
AND rd.state =#{state}
AND (rd.opt_user =#{userOther} OR rd.opt_user =#{user})
AND rd.customer_info =#{a1688order}
AND rd.item =#{pid}
and th.skuID=#{skuid} order by rd.apply_time DESC limit #{page},20

		SELECT count(*) 
from return_display as rd LEFT JOIN
taobao_1688_order_history  as th on rd.tb_id=th.id where 1=1
     
			 AND rd.apply_user=#{nameString} 
	
	
			 AND rd.state=#{state} 
	
	
	 
			 AND rd.1688_shipno=#{a1688Shipno} 
	
		
			AND rd.state =#{state}
		
		
			AND (rd.opt_user =#{userOther} OR rd.opt_user =#{user})
		
		
			AND rd.customer_info =#{a1688order}
		
		
			AND rd.item =#{pid}
		
		and th.skuID=#{skuid}
