mapper.select:
select a.*,b.order_no,b.orderpaytime from (select od.user_id,CAST(sum(if(od.memberFee>=10,od.pay_price-od.memberFee,od.pay_price)) AS DECIMAL(11,2)) as total_amount,ifnull(au.admin_id,0) as sales_id, IFNULL(au.admin_name,'') as sales_name from orderinfo od left join (select admin_r_user.userid,admuser.id as admin_id,admuser.admName as admin_name from admin_r_user,admuser where admin_r_user.adminid = admuser.id) au on od.user_id = au.userid where od.state in(1,2,3,4,5) and od.user_id in(select user_id from orderinfo where IF(memberFee>=10,pay_price-memberFee,pay_price) >= 400 and state in(1,2,3,4,5) GROUP BY user_id) GROUP BY user_id ) a, (select user_id,order_no,orderpaytime from orderinfo where orderid in( select max(orderid) as orderid from orderinfo where state in(1,2,3,4,5) and user_id in(SELECT id FROM USER WHERE email NOT LIKE '%qq.com%' AND email NOT LIKE '%ww.com%' AND email NOT LIKE 'test%' AND email NOT LIKE '%qq.ss' AND email NOT LIKE '%@q.ocm' AND email NOT LIKE '%qqsss.com' AND email NOT LIKE '%csmfg.com%' AND email NOT LIKE '%@sourcing-cn.com%' AND email NOT LIKE '%@china-synergy%' AND email != 'sb33@gmail.com' AND email != 'sbtest@gmail.com' AND email NOT LIKE '%@qq.co%' AND email NOT LIKE '%11.com' AND email NOT LIKE '%@qq.ocm' AND email NOT LIKE '%@163.com' AND email NOT LIKE 'zhouxueyun%') GROUP BY user_id) ) b where a.user_id = b.user_id
and a.user_id = #{userId}
and a.sales_id = #{salesId}
order by b.orderpaytime desc
limit #{startNum},#{limitNum}

select count(0) from (select od.user_id,CAST(sum(if(od.memberFee>=10,od.pay_price-od.memberFee,od.pay_price)) AS DECIMAL(11,2)) as total_amount,ifnull(au.admin_id,0) as sales_id, IFNULL(au.admin_name,'') as sales_name from orderinfo od left join (select admin_r_user.userid,admuser.id as admin_id,admuser.admName as admin_name from admin_r_user,admuser where admin_r_user.adminid = admuser.id) au on od.user_id = au.userid where if(od.memberFee>=10,od.pay_price-od.memberFee,od.pay_price) > 400 and od.state in(1,2,3,4,5) and LENGTH(od.order_no)=16 GROUP BY user_id ) a, (select user_id,order_no,orderpaytime from orderinfo where orderid in( select max(orderid) as orderid from orderinfo where state in(1,2,3,4,5) and LENGTH(order_no) = 16 and user_id in(SELECT id FROM USER WHERE email NOT LIKE '%[图片]qq.com%' AND email NOT LIKE '%ww.com%' AND email NOT LIKE 'test%' AND email NOT LIKE '%qq.ss' AND email NOT LIKE '%@q.ocm' AND email NOT LIKE '%qqsss.com' AND email NOT LIKE '%csmfg.com%' AND email NOT LIKE '%@sourcing-cn.com%' AND email NOT LIKE '%@china-synergy%' AND email != 'sb33@gmail.com' AND email != 'sbtest@gmail.com' AND email NOT LIKE '%@qq.co%' AND email NOT LIKE '%11.com' AND email NOT LIKE '%@qq.ocm' AND email NOT LIKE '%@163.com' AND email NOT LIKE 'zhouxueyun%') GROUP BY user_id) ) b where a.user_id = b.user_id
and a.user_id = #{userId}
and a.sales_id = #{salesId}
