mapper.select:
select id,keyword,count,(select name from 1688_category where category_id=catid1) catid1,(select name from 1688_category where category_id=catid2) catid2, (select name from 1688_category where category_id=catid3) catid3,keyword1,issyn from synonym where isDelete=0
and keyword like CONCAT('%', #{keyword}, '%')
order by id desc limit #{offset},#{pageSize}
