mapper.select:
select y.senddate as lastLoginTime,us.id as userid,us.email,gdf.total_catid,DATE_FORMAT(us.createtime,'%Y-%m-%d %H:%i:%s') as last_add_cart_time, convert(gdf.total_price,decimal(10,2)) as total_price,convert(gdf.average_price,decimal(10,2)) as average_price, us.countryId,zone.country,us.currency,au.saleid,au.salename, '' as last_login_time,ifnull(adm.id,0) as follow_admin_id,ifnull(adm.admName,'') as follow_admin_name, DATE_FORMAT(gdf.follow_time,'%Y-%m-%d %H:%i:%s') as follow_time from (SELECT id,email,countryId,createtime,currency from `user` WHERE firstdiscount=3 and DATE_SUB(CURDATE(), INTERVAL 180 DAY) <= date(createtime)
and DATE_SUB(CURDATE(), INTERVAL 3 DAY) >= date(createtime)
AND is_test=0) us LEFT JOIN ( SELECT gcf.follow_admin_id,gcf.updateTime,gcf.userid,gcf.follow_time,scm.total_catid,scm.total_price,scm.average_price from goods_carconfig gcf inner join (select userid,count(distinct catid) as total_catid,sum(googs_price * googs_number) as total_price, (sum(googs_price * googs_number)/sum(googs_number)) as average_price from shop_car_marketing where userid > 0 group by userid )scm on gcf.userid = scm.userid) gdf on us.id= gdf.userid
LEFT JOIN user_sale au on us.id=au.userid LEFT JOIN (SELECT cid,senddate FROM(SELECT cid,senddate FROM nemail.email_info WHERE cid !=0 AND senddate is not NULL ORDER BY senddate DESC)f GROUP BY cid) y on au.us.id=y.cid left join zone on us.countryId = zone.id left join admuser adm on gdf.follow_admin_id = adm.id where 1=1
and us.id = #{userId}
and us.email = #{userEmail}
and us.id not in(select DISTINCT user_id from orderinfo)
and us.id in(select DISTINCT user_id from orderinfo)
and gdf.follow_admin_id = #{followAdminId}
and au.saleid=#{saleId}
and gdf.total_price >= #{beginMoney}
and gdf.total_price <= #{endMoney}
and gdf.total_price is NULL
and us.countryId = #{countryId}
order by us.createtime desc
limit #{startNum},#{limitNum}

select count(1) FROM (SELECT id,email,countryId,createtime,currency from `user` WHERE firstdiscount=3 and DATE_SUB(CURDATE(), INTERVAL 180 DAY) <= date(createtime)
and DATE_SUB(CURDATE(), INTERVAL 3 DAY) >= date(createtime)
AND is_test=0) us LEFT JOIN ( SELECT gcf.follow_admin_id,gcf.updateTime,gcf.userid,gcf.follow_time,scm.total_catid,scm.total_price,scm.average_price from goods_carconfig gcf inner join (select userid,count(distinct catid) as total_catid,sum(googs_price * googs_number) as total_price, (sum(googs_price * googs_number)/sum(googs_number)) as average_price from shop_car_marketing where userid > 0 group by userid )scm on gcf.userid = scm.userid) gdf on us.id= gdf.userid
LEFT JOIN user_sale au on us.id=au.userid LEFT JOIN (SELECT cid,senddate FROM(SELECT cid,senddate FROM nemail.email_info WHERE cid !=0 AND senddate is not NULL ORDER BY senddate DESC)f GROUP BY cid) y on au.us.id=y.cid left join zone on us.countryId = zone.id left join admuser adm on gdf.follow_admin_id = adm.id where 1=1
and us.id = #{userId}
and us.email = #{userEmail}
and us.id not in(select DISTINCT user_id from orderinfo)
and us.id in(select DISTINCT user_id from orderinfo)
and gdf.follow_admin_id = #{followAdminId}
and au.saleid=#{saleId}
and gdf.total_price >= #{beginMoney}
and gdf.total_price <= #{endMoney}
and gdf.total_price is NULL
and us.countryId = #{countryId}

select * from (select a.user_id,a.create_time as follow_time,a.admin_id,c.admName,b.orderid as order_no, payment_amount,date_format(b.createtime,'%Y-%m-%d %H:%i:%s') as order_pay_time from shop_car_follow a LEFT JOIN (select sum(payment_amount) as payment_amount,userid,orderid,createtime from payment where paystatus = 1 and paytype in(0,1,2,5) group by orderid) b on a.user_id = b.userid and DATE_SUB(a.create_time,INTERVAL 8 HOUR) <=b.createtime and b.createtime <= DATE_ADD(a.create_time,INTERVAL 7 DAY) LEFT JOIN admuser c on a.admin_id = c.id where 1=1 and b.orderid is not null GROUP BY b.orderid) rs where 1=1
and rs.user_id = #{userId}
and rs.admin_id = #{adminId}
and rs.order_no = #{orderNo}
and DATE(rs.order_pay_time) >= DATE(#{orderPayTime})
and DATE(rs.order_pay_time) <= DATE(#{orderPayEndTime})
limit #{startNum},#{limitNum}

select count(1) from (select a.user_id,a.create_time as follow_time,a.admin_id,c.admName,b.orderid as order_no, payment_amount,date_format(b.createtime,'%Y-%m-%d %H:%i:%s') as order_pay_time from shop_car_follow a LEFT JOIN (select sum(payment_amount) as payment_amount,userid,orderid,createtime from payment where paystatus = 1 and paytype in(0,1,2,5) group by orderid) b on a.user_id = b.userid and DATE_SUB(a.create_time,INTERVAL 8 HOUR) <=b.createtime and b.createtime <= DATE_ADD(a.create_time,INTERVAL 7 DAY) LEFT JOIN admuser c on a.admin_id = c.id where 1=1 and b.orderid is not null GROUP BY b.orderid) rs where 1=1
and rs.user_id = #{userId}
and rs.admin_id = #{adminId}
and rs.order_no = #{orderNo}
and DATE(rs.order_pay_time) >= DATE(#{orderPayTime})
and DATE(rs.order_pay_time) <= DATE(#{orderPayEndTime})

select date_format(lg.create_time,'%Y-%m-%d %H:%i:%s') as create_time,lg.follow_code,lg.user_ip,
au.user_id,au.user_email,au.admin_id,au.admin_name
from
email_direct_marketing_log lg,(select admin_r_user.userid as user_id,user.email as user_email,user.follow_code,
admin_r_user.adminid as admin_id,admuser.admName as admin_name
from admin_r_user,admuser,user
where user.id = admin_r_user.userid and admin_r_user.adminid = admuser.id
) au
where lg.follow_code = au.follow_code

    and au.user_id = #{userId}


    and au.user_email = #{userEmail}


    and au.admin_id = #{adminId}


    and lg.follow_code = #{followCode}

order by lg.create_time desc

    limit #{startNum},#{limitNum}

select count(0)
from
email_direct_marketing_log lg,(select admin_r_user.userid as user_id,user.email as user_email,user.follow_code,
admin_r_user.adminid as admin_id,admuser.admName as admin_name
from admin_r_user,admuser,user
where user.id = admin_r_user.userid and admin_r_user.adminid = admuser.id
) au
where lg.follow_code = au.follow_code

    and au.user_id = #{userId}


    and au.user_email = #{userEmail}


    and au.admin_id = #{adminId}


    and lg.follow_code = #{followCode}
