mapper.insert:
TRUNCATE TABLE standard_goods_form_data;
INSERT IGNORE INTO crossshop.standard_goods_form_data (bm_flag,pid, wprice, feeprice, range_price, online_price, online_ali_price, ali_price, valid, ali_pid, createtime) SELECT bm_flag,pid,wprice,feeprice,range_price,SUBSTRING_INDEX(range_price, '-', 1) online_price,SUBSTRING_INDEX(ali_price, '-', 1) online_ali_price,ali_price,valid, ali_pid, NOW() createtime FROM custom_benchmark_ready WHERE valid != 0 AND (range_price IS NOT NULL AND range_price != '') AND (bm_flag=1 || isBenchmark=1) and ali_weight>0;
INSERT IGNORE INTO crossshop.standard_goods_form_data (bm_flag, pid, wprice, feeprice, range_price, online_price, online_ali_price, ali_price, valid, ali_pid, createtime) SELECT a.bm_flag,a.pid,a.wprice,a.feeprice,a.range_price,REPLACE(SUBSTRING_INDEX(a.b_wprice, ' $ ', -1),']','') online_price,SUBSTRING_INDEX(a.ali_price, '-', 1) online_ali_price,a.ali_price,a.valid, a.ali_pid, NOW() createtime FROM ( SELECT bm_flag,pid, wprice, feeprice, SUBSTRING_INDEX(feeprice, ',', 1) b_wprice, range_price,ali_price,valid,ali_pid FROM custom_benchmark_ready WHERE valid != 0 AND (range_price IS NULL OR range_price = '') AND (bm_flag=1 || isBenchmark=1) and ali_weight>0 AND (feeprice IS NOT NULL AND feeprice != '' AND feeprice !='[]') ) a;
INSERT IGNORE INTO crossshop.standard_goods_form_data (bm_flag,pid, wprice, feeprice, range_price, online_price, online_ali_price, ali_price, valid, ali_pid, createtime) SELECT a.bm_flag,a.pid,a.wprice,a.feeprice,a.range_price,REPLACE(SUBSTRING_INDEX(a.b_wprice, ' $ ', -1),']','') online_price,SUBSTRING_INDEX(a.ali_price, '-', 1) online_ali_price,a.ali_price,a.valid, a.ali_pid,NOW() createtime FROM ( SELECT bm_flag,pid, wprice, feeprice, SUBSTRING_INDEX(wprice, ',', 1) b_wprice, range_price,ali_price,valid,ali_pid FROM custom_benchmark_ready WHERE valid != 0 AND (range_price IS NULL OR range_price = '') AND (bm_flag=1 || isBenchmark=1) and ali_weight>0 AND (feeprice IS NULL OR feeprice = '' OR feeprice ='[]') AND (wprice IS NOT NULL AND wprice != '' AND wprice !='[]') ) a;
INSERT IGNORE INTO crossshop.standard_goods_form_data (bm_flag, pid, wprice, feeprice, range_price, online_price, online_ali_price, ali_price, valid, ali_pid, createtime) SELECT a.bm_flag,a.pid,a.wprice,a.feeprice,a.range_price,price online_price,SUBSTRING_INDEX(a.ali_price, '-', 1) online_ali_price,a.ali_price,a.valid, a.ali_pid,NOW() createtime FROM ( SELECT bm_flag,pid, wprice, feeprice, price, range_price,ali_price,valid,ali_pid FROM custom_benchmark_ready WHERE valid != 0 AND (range_price IS NULL OR range_price = '') AND (bm_flag=1 || isBenchmark=1) and ali_weight>0 AND (feeprice IS NULL OR feeprice = '' OR feeprice ='[]') AND (wprice IS NULL OR wprice = '' OR wprice ='[]') ) a;
UPDATE standard_goods_form_data SET flag = 2 WHERE online_price >= online_ali_price * 1.1;
UPDATE standard_goods_form_data SET flag = 1 WHERE online_ali_price * 0.8 >= online_price;

