mapper.select:
		SELECT a.id, a.count, a.leftCount, a.describe, a.value, a.from, a.to, a.type, a.valid, a.userid, a.mqlog, a.createtime, b.admName username, a.share_flag shareFlag
		FROM tab_coupon_new a
		LEFT JOIN admuser b ON a.userid = b.id


				 a.type = #{typeCode}


                AND a.valid = #{valid}


                AND a.to >= NOW()


                AND NOW() > a.to


                AND (site = 0 OR site = #{couponSite})


        ORDER BY createtime DESC
		LIMIT #{startBars},#{rows}

SELECT COUNT(1) FROM tab_coupon_new a
a.type_code = #{typeCode}
AND a.valid = #{valid}
AND a.to >= NOW()
AND NOW() > a.to
AND (site = 0 OR site = #{couponSite})

mapper.update:
UPDATE tab_coupon_user
SET valid = 0
WHERE couponCode = #{couponCode} AND userid = #{userid}

UPDATE tab_coupon_new
SET valid = #{valid}
WHERE id = #{couponCode}

